FBProgram.STSource:
(*Program for getting Real time clock for PLC last stop*)

(*Second*)
HMI_SW[20] :=%SW54;

(*Hour and minute*)
HMI_SW[21]:=%SW55;

(*Month and date*)
HMI_SW[22] :=%SW56;

(*Year*)
HMI_SW[23] :=%SW57;

(*Day of week and (MSB) the cause code of the last stop (LSB)*)
HMI_SW[24] :=%SW58;

(*Program getting the value of current request communication to CPU*)
HMI_SW[36] :=%SW87;

(*Program getting the value of maximum request communication to CPU*)
HMI_SW[25] :=%SW90;

(*Program getting the percentage of used communication capacity from one CPU*)
HMI_SW[26] := REAL_TO_INT ((DIV (INT_TO_REAL (HMI_SW[36]), INT_TO_REAL(HMI_SW[25]))) * INT_TO_REAL(100));

(*program for getting commercial version of PLC processor*)
HMI_SW[27]:= %SW14;

(*program for getting PLC processor patch version*)
HMI_SW[28]:= %SW15;

(*program for getting PLC firmware version*)
HMI_SW[29]:= %SW16;

(**Program for getting Real time clock from PLC*)

(*Day of week*)
HMI_SW[30] :=%SW49;

(*Second*)
HMI_SW[31] :=%SW50;

(*Hour and minute*)
HMI_SW[32] :=%SW51;

(*Month and date*)
HMI_SW[33] :=%SW52;

(*Year*)
HMI_SW[34] :=%SW53;

(* Task Status *)
HMI_SW[35].0:=%S30;
HMI_SW[35].1:=%S31;
HMI_SW[35].2:=%S32;
HMI_SW[35].3:=%S33;
HMI_SW[35].4:=%S34;
HMI_SW[35].5:=%S35;
